parameters.functionCode:
const geminiResp = items[0].json.candidates[0].content.parts[0].text;
// Remove markdown code blocks if present
const cleanJson = geminiResp.replace(/```json/g, '').replace(/```/g, '').trim();
let analysis = {};
try {
  analysis = JSON.parse(cleanJson);
} catch (e) {
  analysis = { urgency: 'Medium', category: 'General', summary: 'Could not parse AI response', suggested_action: 'Manual Review' };
}

return [{
  json: {
    ...items[0].json,
    analysis
  }
}];

